import React from "react";
import "./login.css";

import "../../css/global.css";


  const Funcionarios = () => {
    const {
      funcionarios,
      salvarNovoFuncionario,
      salvarAlteracoes,
      excluirFuncionario,
    } = useFuncionarios();
  
    const {
      modalFuncionario,
      showModal,
      isEditing,
      setIsEditing,
      abrirModal,
      abrirModalCadastro,
      fecharModal,
      handleInputChange,
      atualizarObra,
      adicionarObra,
      removerObra,
    } = useModal();

  return (
    <div className="funcionarios">
      <h1>Lista de Funcionários</h1>
      <button onClick={abrirModalCadastro} className="btn-cadastrar">
        + Cadastrar Funcionário
      </button>
      <div className="tabela-funcionarios-container">
        <table className="tabela-funcionarios">
          <thead>
            <tr>
              <th>Foto</th>
              <th>Nome</th>
              <th>Função</th>
              <th>Salário</th>
              <th>Diária</th>
              <th>Ativo</th>
              <th>Obra</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {funcionarios.map((func) => (
              <tr key={func.id}>
                <td>
                  <img
                    src={func.imagem}
                    alt={func.nome}
                    className="foto-funcionario"
                  />
                </td>
                <td>{func.nome}</td>
                <td>{func.funcao}</td>
                <td>R$ {func.salario}</td>
                <td>R$ {func.diaria}</td>
                <td>{func.ativo ? "Sim" : "Não"}</td>
                <td>{func.obra}</td>
                <td>
                  <button onClick={() => abrirModal(func)}>Visualizar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>        
      {showModal && modalFuncionario && (
        <div className="modal">
          <div className="modal-content">
            <button className="modal-close" onClick={fecharModal}>
              X
            </button>
            <h2>
              {modalFuncionario.id === null
                ? "Cadastrar Funcionário"
                : isEditing
                ? "Editar Funcionário"
                : "Detalhes do Funcionário"}
            </h2>
            <div className="modal-body">
              <div className="modal-left">
                <img
                  src={modalFuncionario.imagem}
                  alt={modalFuncionario.nome}
                  className="foto-detalhe"
                />
                <div className="form-group">
                  <p>
                    <strong>Nome:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="nome"
                        value={modalFuncionario.nome}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.nome
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Função:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="funcao"
                        value={modalFuncionario.funcao}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.funcao
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Salário:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="number"
                        name="salario"
                        value={modalFuncionario.salario}
                        onChange={handleInputChange}
                      />
                    ) : (
                      `R$ ${modalFuncionario.salario}`
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Diária:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="number"
                        name="diaria"
                        value={modalFuncionario.diaria}
                        onChange={handleInputChange}
                      />
                    ) : (
                      `R$ ${modalFuncionario.diaria}`
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Email:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="email"
                        name="email"
                        value={modalFuncionario.email}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.email
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Contato:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="contato"
                        value={modalFuncionario.contato}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.contato
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>CPF:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="cpf"
                        value={modalFuncionario.cpf}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.cpf
                    )}
                  </p>
                </div>
              </div>
              <div className="modal-right">
                <h3>Histórico</h3>
                <ul>
                  {modalFuncionario.historico.map((obra, index) => (
                    <li key={index} className={index % 2 === 0 ? "historico-item branco" : "historico-item cinza"}>
                      {isEditing ? (
                        <>
                        <div className="form-group">
                          <input
                            type="text"
                            value={obra.nome}
                            onChange={(e) => atualizarObra(index, "nome", e.target.value)}
                            placeholder="Nome da Obra"
                          />
                        </div>
                        <div className="form-group">
                          <input
                            type="number"
                            value={obra.ganho}
                            onChange={(e) => atualizarObra(index, "ganho", e.target.value)}
                            placeholder="Ganho"
                          />
                        </div>
                        <div className="form-group">
                          <input
                            type="text"
                            value={obra.datas}
                            onChange={(e) => atualizarObra(index, "datas", e.target.value)}
                            placeholder="Período"
                          />
                        </div>
                          <div className="buttons-removeObra">
                            <button onClick={() => removerObra(index)}>Remover</button>
                          </div>
                        </>
                      ) : (
                        <>
                          <p><strong>Obra:</strong> {obra.nome}</p>
                          <p><strong>Ganho:</strong> R$ {obra.ganho}</p>
                          <p><strong>Período:</strong> {obra.datas}</p>
                        </>
                      )}
                    </li>
                  ))}
                </ul>
                {isEditing && (
                  <div className="buttons-removeObra">
                    <button onClick={adicionarObra}>Adicionar Obra</button>
                  </div>
                )}
              </div>
            </div>
            <div className="modal-buttons">
              {isEditing ? (
                <>
                  {modalFuncionario.id === null ? (
                    <button onClick={salvarNovoFuncionario}>Cadastrar</button>
                  ) : (
                    <button onClick={salvarAlteracoes}>Salvar</button>
                  )}
                  <button onClick={fecharModal}>Cancelar</button>
                </>
              ) : (
                <>
                  <button onClick={() => setIsEditing(true)}>Editar</button>
                  <button
                    onClick={() => {
                      const confirmacao = window.confirm(
                        "Deseja realmente excluir este funcionário?"
                      );
                      if (confirmacao) {
                        excluirFuncionario(modalFuncionario.id);
                        fecharModal();
                      }
                    }}
                  >
                    Excluir
                  </button>
                  <button onClick={() => alert("Comprovantes!")}>Comprovantes</button>
                </>
              )}
            </div>
          </div>
        </div>
        
      )}

    </div>
  );
};

export default Funcionarios;
