import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { usuariosApi } from "../../api";

// Componente de Entrada Reutilizável
const Input = ({ label, type, name, value, onChange, required = true }) => (
  <div className="mb-4">
    <label htmlFor={name} className="block text-gray-700 mb-2">
      {label}
    </label>
    <input
      type={type}
      id={name}
      name={name}
      value={value}
      onChange={onChange}
      className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
      required={required}
    />
  </div>
);

// Botão Reutilizável
const Button = ({ text, onClick, type = "button", className }) => (
  <button
    type={type}
    onClick={onClick}
    className={`px-4 py-2 rounded transition ${className}`}
  >
    {text}
  </button>
);

const Login = () => {
  const [formData, setFormData] = useState({ email: "", senha: "" });
  const [cadastroData, setCadastroData] = useState({
    nome: "",
    email: "",
    senha: "",
  });
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e, isCadastro = false) => {
    const { name, value } = e.target;
    isCadastro
      ? setCadastroData({ ...cadastroData, [name]: value })
      : setFormData({ ...formData, [name]: value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const response = await usuariosApi.post("/", formData);
      const usuario = response.data.usuario;
      if (!usuario) throw new Error("Credenciais inválidas");
      localStorage.setItem("usuario", JSON.stringify(usuario));
      navigate("/usuarios");
    } catch (err) {
      setError(err.response?.data?.message || "Erro ao realizar login.");
      setTimeout(() => setError(null), 5000); // Remove o erro após 5 segundos
    }
  };

  const handleCadastro = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);
    try {
      await usuariosApi.post("/usuarios", cadastroData);
      setSuccessMessage("Usuário cadastrado com sucesso!");
      setIsModalOpen(false);
      setTimeout(() => setSuccessMessage(null), 5000); // Remove a mensagem de sucesso após 5 segundos
    } catch (err) {
      setError("Erro ao cadastrar usuário. Tente novamente.");
      setTimeout(() => setError(null), 5000); // Remove o erro após 5 segundos
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">Login</h1>
        <form onSubmit={handleLogin}>
          <Input
            label="Email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
          />
          <Input
            label="Senha"
            type="password"
            name="senha"
            value={formData.senha}
            onChange={handleInputChange}
          />
          {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
          {successMessage && (
            <p className="text-green-500 text-sm mb-4">{successMessage}</p>
          )}
          <Button
            type="submit"
            text="Entrar"
            className="w-full bg-blue-500 text-white hover:bg-blue-600"
          />
        </form>
        <Button
          text="Cadastrar Novo Login"
          onClick={() => setIsModalOpen(true)}
          className="w-full mt-4 bg-green-500 text-white hover:bg-green-600"
        />
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
            <h2 className="text-xl font-bold mb-4 text-center">
              Cadastrar Usuário
            </h2>
            <form onSubmit={handleCadastro}>
              <Input
                label="Nome"
                type="text"
                name="nome"
                value={cadastroData.nome}
                onChange={(e) => handleInputChange(e, true)}
              />
              <Input
                label="Email"
                type="email"
                name="email"
                value={cadastroData.email}
                onChange={(e) => handleInputChange(e, true)}
              />
              <Input
                label="Senha"
                type="password"
                name="senha"
                value={cadastroData.senha}
                onChange={(e) => handleInputChange(e, true)}
              />
              {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
              <div className="flex justify-between">
                <Button
                  text="Cancelar"
                  onClick={() => setIsModalOpen(false)}
                  className="bg-gray-300 text-gray-700 hover:bg-gray-400"
                />
                <Button
                  type="submit"
                  text="Cadastrar"
                  className="bg-green-500 text-white hover:bg-green-600"
                />
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
