import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { usuariosApi } from "../../api";


const Login = () => {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault()
    setError(null)
  
    try {
      const response = await usuariosApi.post('/usuarios', { email, senha })
  
      // Verifica se a resposta contém o usuário
      const usuario = response.data.usuario
  
      if (!usuario) {
        setError('Credenciais inválidas')
        return
      }
  
      // Salva informações do usuário no localStorage (opcional)
      localStorage.setItem('usuario', JSON.stringify(usuario))
  
      // Redireciona para a página principal
      navigate('/usuarios')
    } catch (err) {
      setError(err.response?.data?.message || 'Erro ao realizar login.')
      console.error('Erro no login:', err)
    }
  }
  const navigateCadastro = () => {
    navigate("/cadastro");
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">Login</h1>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="senha" className="block text-gray-700 mb-2">
              Senha
            </label>
            <input
              type="password"
              id="senha"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
          </div>
          {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
          >
            Entrar
          </button>
        </form>
        <button
          onClick={navigateCadastro}
          className="mt-4 w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition"
        >
          Cadastrar Novo Login
        </button>
      </div>
    </div>
  );
};

export default Login;
