export const useModal = () => {
  const [modalFuncionario, setModalFuncionario] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  const abrirModal = (funcionario) => {
    setModalFuncionario({ ...funcionario });
    setShowModal(true);
    setIsEditing(false);
  };

  const abrirModalCadastro = () => {
    setModalFuncionario({ ...novoFuncionarioTemplate });
    setShowModal(true);
    setIsEditing(true);
  };

  const fecharModal = () => {
    setModalFuncionario(null);
    setShowModal(false);
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setModalFuncionario((prev) => ({ ...prev, [name]: value }));
  };

  const atualizarObra = (index, campo, valor) => {
    const novoHistorico = [...modalFuncionario.historico];
    novoHistorico[index][campo] = valor;
    setModalFuncionario((prev) => ({ ...prev, historico: novoHistorico }));
  };

  const adicionarObra = () => {
    const novaObra = { nome: "", ganho: 0, datas: "" };
    setModalFuncionario((prev) => ({
      ...prev,
      historico: [...prev.historico, novaObra],
    }));
  };

  const removerObra = (index) => {
    const novoHistorico = modalFuncionario.historico.filter(
      (_, i) => i !== index
    );
    setModalFuncionario((prev) => ({ ...prev, historico: novoHistorico }));
  };

  return {
    modalFuncionario,
    setModalFuncionario,
    showModal,
    setShowModal,
    isEditing,
    setIsEditing,
    abrirModal,
    abrirModalCadastro,
    fecharModal,
    handleInputChange,
    atualizarObra,
    adicionarObra,
    removerObra,
  };
};
