import React from "react";
import "./usuarios.css";
import { useFuncionarios, useModal } from "./modalUtils";
import "../../css/global.css";


  const Funcionarios = () => {
    const {
      funcionarios,
      salvarNovoFuncionario,
      salvarAlteracoes,
      excluirFuncionario,
    } = useFuncionarios();
  
    const {
      modalFuncionario,
      showModal,
      isEditing,
      setIsEditing,
      abrirModal,
      abrirModalCadastro,
      fecharModal,
      handleInputChange,
      atualizarObra,
      adicionarObra,
      removerObra,
    } = useModal();

  return (
    <div className="funcionarios">
      <h1>Lista de Usuarios</h1>
      <button onClick={abrirModalCadastro} className="btn-cadastrar">
        + Cadastrar Usuario
      </button>
      <div className="tabela-funcionarios-container">
        <table className="tabela-funcionarios">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Ramal</th>
              <th>Senha</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {funcionarios.map((func) => (
              <tr key={func.id}>
                <td>{func.id}</td>
                <td>{func.nome}</td>
                <td>R$ {func.ramal}</td>
                <td>R$ {func.senha}</td>
                <td>{func.status ? "Ativo" : "Inativo"}</td>
                <td>
                  <button onClick={() => abrirModal(func)}>Visualizar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>        
      {showModal && modalFuncionario && (
        <div className="modal">
          <div className="modal-content">
            <button className="modal-close" onClick={fecharModal}>
              X
            </button>
            <h2>
              {modalFuncionario.id === null
                ? "Cadastrar Usuario"
                : isEditing
                ? "Editar Usuario"
                : "Detalhes do Usuario"}
            </h2>
            <div className="modal-body">
              <div className="modal-left">
                <div className="form-group">
                  <p>
                    <strong>Nome:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="nome"
                        value={modalFuncionario.nome}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.nome
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Função:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="funcao"
                        value={modalFuncionario.funcao}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.funcao
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Salário:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="number"
                        name="salario"
                        value={modalFuncionario.salario}
                        onChange={handleInputChange}
                      />
                    ) : (
                      `R$ ${modalFuncionario.salario}`
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Diária:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="number"
                        name="diaria"
                        value={modalFuncionario.diaria}
                        onChange={handleInputChange}
                      />
                    ) : (
                      `R$ ${modalFuncionario.diaria}`
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Email:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="email"
                        name="email"
                        value={modalFuncionario.email}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.email
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>Contato:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="contato"
                        value={modalFuncionario.contato}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.contato
                    )}
                  </p>
                </div>
                <div className="form-group">
                  <p>
                    <strong>CPF:</strong>{" "}
                    {isEditing ? (
                      <input
                        type="text"
                        name="cpf"
                        value={modalFuncionario.cpf}
                        onChange={handleInputChange}
                      />
                    ) : (
                      modalFuncionario.cpf
                    )}
                  </p>
                </div>
              </div>
            </div>
            <div className="modal-buttons">
              {isEditing ? (
                <>
                  {modalFuncionario.id === null ? (
                    <button onClick={salvarNovoFuncionario}>Cadastrar</button>
                  ) : (
                    <button onClick={salvarAlteracoes}>Salvar</button>
                  )}
                  <button onClick={fecharModal}>Cancelar</button>
                </>
              ) : (
                <>
                  <button onClick={() => setIsEditing(true)}>Editar</button>
                  <button
                    onClick={() => {
                      const confirmacao = window.confirm(
                        "Deseja realmente excluir este funcionário?"
                      );
                      if (confirmacao) {
                        excluirFuncionario(modalFuncionario.id);
                        fecharModal();
                      }
                    }}
                  >
                    Excluir
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
        
      )}

    </div>
  );
};

export default Funcionarios;
